#pragma once
#include "Runtime/Launch/Resources/Version.h"

#define UE_ENGINE_VER_INT(MAJOR, MINOR) ((MAJOR)*100+(MINOR))

#define UE_ENGINE_VER_CURRENT (UE_ENGINE_VER_INT(ENGINE_MAJOR_VERSION, ENGINE_MINOR_VERSION))

#define UE_ENGINE_VER_LESS_THAN(MAJOR, MINOR) (UE_ENGINE_VER_CURRENT < UE_ENGINE_VER_INT(MAJOR, MINOR))

#define UE_ENGINE_VER_GREAT_THAN(MAJOR, MINOR) (UE_ENGINE_VER_CURRENT > UE_ENGINE_VER_INT(MAJOR, MINOR))

#define UE_ENGINE_VER_EQUALTO(MAJOR, MINOR) (UE_ENGINE_VER_CURRENT == UE_ENGINE_VER_INT(MAJOR, MINOR))
